	(29, 1, 10, 'nav', 'css', NULL, 'nav {\n	display:flex;\n	flex-wrap:wrap;\n	justify-content:space-between;\n	align-content:space-between;\n	font-family:\"Protest Revolution\";\n	font-size: 1.5em;\n	 background:linear-gradient(45deg, rgba(237, 198, 112, 0.6), rgba(237, 198,112, 0.4));\nbackdrop-filter: blur(4px);\nbox-shadow: 0 0 20px #000;\n    border-radius: 15px;\n    border: 2px solid rgba(93, 68, 0, 0.5);\n		/*background: rgba( 255, 255, 0, 0.2);*/\n	color: var(--color-on-secondary-container);\n	margin:0;\n	\n}\n\n#nav-sub {\n	display:flex;\n	padding: 15px 30px;\n	color:var(--color-on-primary);\n	text-align: center;\n	border-radius: 20px;\n	text-decoration: none;\n	margin:0;\n	align-self:baseline;\n	transition: background 0.2s ease-out;\n	justify-self:start;\n	li {\n		list-style:none;\n	}\n	\n}\n\n.nav-logo{\nmargin:0;\nbox-sizing:border-box;\npadding-left:1em;\njustify-self:start;\nfont-size:larger;\n}\n\n.nav-signin{\npadding: 15px 30px;\njustify-self:end;\nalign-self:baseline;\n}\n\nnav a{\n	\n	padding: 15px 30px;\n	color: var(--color-on-primary);\n	border-radius: 20px;\n	text-decoration: none;\n	transition: background 0.2s ease-out;\n\n}\n\nnav a:hover{\n	background:rgba(255, 0, 0, 0.5);;\n	color:var(--color-primary);\n}\n\n', '', 1720421376, 1, 'n', 0, '', 'n', 'n', 'o', 0, 'n', 'y'),
	(30, 1, 10, 'staff_card', 'css', NULL, '.about {\n	\n	display:flex;\n    width:100%;\n    flex-wrap:wrap;\n    flex-direction:column;\n    color:var(--color-on-primary);\n    align-items:start;\n    justify-content:center;\n    height:min-content;\n\n}\n\n.staff-card {\n  width: 250px;\n  height: auto;\n  padding: 20px;\n  background: linear-gradient(\n    45deg,\n    rgba(255, 255, 255, 0.1),\n    rgba(255, 255, 255, 0.2)\n  );\n  backdrop-filter: blur(4px);\n  box-shadow: 0 0 20px #000;\n  border-top: 2px solid rgba(255, 255, 255, 0.25);\n  border-left: 2px solid rgba(255, 255, 255, 0.2);\n}\n\n.staff-card img {\n  width: 150px;\n  border-radius: 50%;\n  border: 2px solid #fff;\n}\n\n.staff-card h2 {\n  color: #fff;\n  font-weight: normal;\n  font-size: 25px;\n}\n\n.staff-card p {\n  color: var(--color-on-primary);\n  text-align: justify;\n  font-size: 15px;\n}\n\n.staff-card button {\n  display: inline-block;\n  width: 50px;\n  height: 50px;\n  font-size: 22px;\n  border-radius: 50%;\n  border: none;\n  margin-right: 10px;\n  color: #fff;\n  transition: 0.4s all;\n  cursor: pointer;\n}\n\n.about-story{\nmargin:15px;\npadding:20px;\n}\n\n.about-title{\nmargin:0;\npadding:015px\n}', NULL, 1720377430, 1, 'n', 0, '', 'n', 'n', 'o', 0, 'n', 'y'),
	(31, 1, 10, 'homepage', 'css', NULL, '.homepage_logo {\ndisplay:block;\nmargin-top:-20px;\nmargin-bottom:5px;\noverflow:visible;\nmargin-inline: auto;\nobject-fit:cover;\n animation: logoentrance 2s ease-in-out 1 normal forwards;\njustify-self:center;\n}\n\n\n\n\n\n.call-to-action{\nheight:85vh;\nwidth:100%;\nposition:relative;\nbox-sizing:border-box;\npadding:0;\nfont-family:\"Protest Revolution\";\ndisplay:grid;\ngrid-template-columns:1fr;\ngrid-template-rows: .10fr .75fr .15fr;\ngrid-auto-flow: column;\njustify-items: center;\n\nh1 {\n	text-align:center;\n	width:100%;\n	padding:0;\n	font-size: 4em;\n	font-weight:bolder;\n	color:var(--color-on-secondary);\n}\n}\n\n\n\n.goals{\nwidth:100%;\nmargin:0 auto;\npadding:0;\n\n}\n\n\n.button-div{\nheight:min-content;\nmargin-inline:auto;\n\n}\n\nbutton {\n	transition: transform 0.3s ease;\n	background: rgba( 255, 255, 0, 0.2);\n	box-shadow: 0 4px 30px 0 rgba( 31, 38, 135, 0.425 );\n	backdrop-filter: blur( 3.1px );\n	-webkit-backdrop-filter: blur( 5px );\n	border-radius: 10px;\n	border: 1px solid rgba( 255, 255, 0, 1 );\n	height:75%;						\n	padding: 16px 40px;\n	padding-bottom:5px;\n	font-size: 56px;	\n	cursor: pointer;\n	font-family: \"Protest Revolution\";\n\n	\n}\nbutton:hover {\n	background: rgba( 175, 140, 61, 0.4 );\n	box-shadow: 0 8px 32px 0 rgba( 31, 38, 135, 0.7 );\n	backdrop-filter: blur( 4px );\n	transform: scale(1.2);\n}\n\n@keyframes logoentrance {\n	0% {\n		opacity: 0;\n		transform: rotate(-540deg) scale(0);\n		transform-origin: 50% 0;\n	}\n\n	100% {\n		opacity: 1;\n		transform: rotate(0) scale(1);\n		transform-origin: 50% 0;\n	}\n}\n', NULL, 1720377430, 1, 'n', 0, '', 'n', 'n', 'o', 0, 'n', 'y'),
	(32, 1, 10, 'footer', 'css', NULL, '/* Glassmorphic Footer Styles */\n.glassmorphic-footer {\n   background:linear-gradient(45deg, rgba(237, 198, 112, 0.6), rgba(237, 198,112, 0.4));\nbackdrop-filter: blur(4px);\nbox-shadow: 0 0 20px #000;\n    border-radius: 15px;\n    border: 2px solid rgba(93, 68, 0, 0.5);\n    color: #F2F1DC; /* Neutral color */\n    width:100vw;\n    font-family:\"Protest Revolution\";\n   margin-top:5em;\n   \n  \n\n  \n}\n\n\n.footer-contnt{\nwidth:100vw;\ndisplay:grid;\ngrid-template-columns:repeat(3, 1fr);\ngrid-template-rows:auto;\ngrid-auto-flow:column;\ngap:5px;\nmargin-left:20px;\n}\n\n.footer-section.first-section{\ngrid-column-start:1;\ngrid-column-end:2;\ndisplay:flex;\nheight:min-content;\nalign-items:start;\nflex-direction:column;\nwidth: 100%;\n}\n\n.footer-section.second-section{\ndisplay:flex;\nflex-direction:column;\ngrid-column-start:2;\ngrid-column-end:3;\nwidth:100%;\n\n\n}\n\n.footer-section.third-section{\ndisplay:flex;\ngrid-column-start:3;\ngrid-column-end: 4;\nwidth:100%;\n}\n\n.footer-section {\np{\n	font-size:1em;\n}\n}\n\n.footer-section > h3 {\n    color: var(--color-on-primary); /* Primary color */\n    font-size: 1.5em;\n    box-sizing:border-box;\n    margin:0;\n    padding:0;\n    \n   \n}\n\n.footer-section p,\n.footer-section a {\n    color: var(--color-on-primary-container); /* Neutral color */\n    text-decoration: none;\n    \n}\n\n.footer-section a:hover {\n    color: #499CA6; /* Secondary color */\n}\n\n.social-icons, .footer-copyright, .footer-links {\n    display:block;\n    box-sizing: border-box;\n   \n}\n.footer-section.second-section{\n    h3{\n        margin-bottom:10px;\n    }\n}\n.social icons {\n   \n   padding-top:10px;\n    \n}\n\n.social-icons > a {\n    padding: 5px 10px;\n    border-radius: 5px;\n    background: rgba(54, 55, 64, 0.5); /* Tertiary color with transparency */\n    transition: background 0.3s ease;\n    \n}\n\n.footer-links > a {\n\n    border-radius: 5px;\n    background: rgba(54, 55, 64, 0.5); /* Tertiary color with transparency */\n    transition: background 0.3s ease;\n}\n.social-icons a:hover {\n    background: rgba(73, 156, 166, 0.5); /* Secondary color with transparency */\n}\n\n.footer-links a:hover{\n background: rgba(73, 156, 166, 0.5); /* Secondary color with transparency */\n}\n@media (max-width: 768px) {\n    .footer-content {\n        flex-direction: column;\n        align-items: center;\n    }\n\n    .footer-section {\n        margin: 10px 0;\n    }\n}', '', 1720826066, 1, 'n', 0, '', 'n', 'n', 'o', 0, 'n', 'y'),
